import React, { useState } from 'react';
import './Forms.css';
import Modal from '../Modal/Modal';

const Forms = () => {
    const [isActiveForm, setIsActiveForm] = useState(false);
    const [title, setTitle] = useState("");
    const [text, setText] = useState("");
    const [note, setNote] = useState("");
    const [isModalOpen, setIsModalOpen] = useState(false);

    const clearInputs = () => {
        setTitle("");
        setText("");
        setNote("");
    };

    const handleFormClick = () => {
        setIsActiveForm(true);
    };

    const handleClose = () => {
        clearInputs();
        setIsActiveForm(false);
    };

    const handleNoteChange = (e) => {
        setNote(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Form submitted with title:", title, "and text:", text);
        handleClose();
    };

    return (
        <div className="main-content">
            <div
                className={`inactive-form-container ${isActiveForm ? "hidden" : ""}`}
                onClick={handleFormClick}
            >
                <form className="inactive-form">
                    <input
                        type="text"
                        placeholder="Take a note..."
                        value={note}
                        onChange={handleNoteChange}
                    />
                    <div className="inactive-form-icons">
                        {/* Icons here */}
                    </div>
                </form>
            </div>

            <div className={`active-form-container ${isActiveForm ? "visible" : ""}`}>
                <form className="active-form" onSubmit={handleSubmit}>
                    <div className="note-title">
                        <input
                            type="text"
                            placeholder="Title"
                            value={title}
                            onChange={(e) => setTitle(e.target.value)}
                        />
                        <span className="material-symbols-outlined nav-icon note-pin">keep</span>
                    </div>
                    <div className="note-text">
                        <input
                            type="text"
                            placeholder="Text..."
                            value={text}
                            onChange={(e) => setText(e.target.value)}
                        />
                    </div>
                    <div className="note-footer">
                        <div className="footer-icons">
                            {/* Footer icons here */}
                        </div>
                        <div className="footer-button">
                            <button type="button" className="close-button" onClick={handleClose}>
                                Close
                            </button>
                            <button type="submit" className="submit-button">
                                Save
                            </button>
                        </div>
                    </div>
                </form>
            </div>

            {isModalOpen && <Modal />}
        </div>
    );
};

export default Forms;
